{"settings":"{
  "security.workspace.trust.untrustedFiles": "open",
  // VIM
  "vim.cursorStylePerMode.normal": "block",
  "vim.cursorStylePerMode.visual": "line-thin",
  "vim.hlsearch": true,
  "vim.searchHighlightColor": "#E52165",
  "vim.searchHighlightTextColor": "#ffffff",
  "vim.ignorecase": true,
  "vim.foldfix": true,
  "vim.surround": true,
  "vim.vimrc.enable": true,
  "vim.vimrc.path": "~/.vimrc",
  // EDITOR
  "editor.wordBasedSuggestions": "off",
  "editor.fontSize": 12,
  "editor.fontLigatures": true,
  "editor.fontWeight": "450",
  "editor.minimap.enabled": false,
  // Fira Code settings https://github.com/tonsky/FiraCode/wiki/VS-Code-Instructions
  "editor.fontFamily": "Fira Code",
  "editor.suggest.showConstants": true,
  "editor.suggest.showSnippets": true,
  "editor.suggest.showConstructors": true,
  "editor.suggest.showFolders": true,
  "editor.insertSpaces": false,
  "editor.acceptSuggestionOnEnter": "smart",
  "editor.autoSurround": "brackets",
  "editor.cursorStyle": "underline",
  "editor.formatOnSave": true,
  // SUGGEST
  "editor.quickSuggestionsDelay": 1000,
  "editor.quickSuggestions": {
    "other": "on",
    "comments": "off",
    "strings": "off"
  },
  "editor.formatOnSaveMode": "file",
  // GENERAL
  "debug.console.wordWrap": true,
  // TODO HIGHLIGHT
  "todohighlight.isEnable": true,
  "todohighlight.isCaseSensitive": true,
  "todohighlight.keywords": ["TODO", "REFACTORING", "FIXME"],
  "todohighlight.defaultStyle": {
    "backgroundColor": "white"
  },
  // -----------------------
  "telemetry.telemetryLevel": "off",
  "window.title": "DevHub - ${folderName}",
  "window.titleBarStyle": "custom", // Title bar will change color to match the color theme
  "window.closeWhenEmpty": false,
  "explorer.confirmDelete": false,
  "explorer.compactFolders": true,
  // PHP
  "php.suggest.basic": false,
  // C++
  "[cpp]": {
    "editor.defaultFormatter": "ms-vscode.cpptools"
  },
  // GOLANG
  "[go]": {
    "editor.defaultFormatter": "golang.go",
    "editor.formatOnSave": true,
    "editor.quickSuggestions": {
      "strings": "on"
    },
    "editor.rulers": [120],
    "editor.wordWrap": "bounded",
    "editor.wordWrapColumn": 160,
    "editor.codeActionsOnSave": {
      "source.organizeImports": "explicit"
    }
  },
  "go.formatTool": "gofumpt",
  "go.formatFlags": ["-w"],
  "go.lintTool": "golangci-lint",
  // "go.lintFlags": [
  //   "--fast",
  //   "--config",
  //   "/Users/denis/Desktop/scala/s3proxyserver/.golangci.yml"
  // ],
  "gopls": {
    "ui.semanticTokens": true,
    "ui.completion.usePlaceholders": true,
    "ui.completion.experimentalPostfixCompletions": false
  },
  // "go.buildTags": "integration",
  "go.useLanguageServer": true,
  "go.lintOnSave": "package",
  "go.testFlags": [
    "-count",
    "1",
    "-race",
    "-parallel",
    "4",
    "-timeout",
    "1m",
    "./..."
  ],
  "go.coverOnTestPackage": true,
  "go-struct-tag.cases": ["snake"],
  "[javascript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "files.autoSave": "afterDelay",
  "[python]": {
    "editor.insertSpaces": true,
    "editor.tabSize": 4
  },
  "workbench.colorCustomizations": {
    "[Dracula Theme]": {
      "editorLineNumber.foreground": "#FFFFFF",
      "editor.lineHighlightBackground": "#44475a",
      "editor.selectionBackground": "#F05A7E",
      "editor.selectionForeground": "#FFFFFF",
      "editorOverviewRuler.commentForeground": "#FFFFFF",
      "tab.inactiveForeground": "#FFFFFF",
      "tab.activeForeground": "#91e3d5",
      "tab.activeBackground":"#ce097571"
    }
  },
  "workbench.colorTheme": "Dracula Theme",
  "workbench.iconTheme": "material-icon-theme",
  "terminal.external.linuxExec": "Terminal.app",
  "hexeditor.columnWidth": 16,
  "hexeditor.showDecodedText": true,
  "hexeditor.defaultEndianness": "little",
  "hexeditor.inspectorType": "aside",
  "window.commandCenter": false,
  "files.associations": {
    "*.go": "go",
    "*.vuex": "vue",
    "*.tsx": "typescriptreact",
    "*.jsx": "javascriptreact"
  },
  "diffEditor.renderSideBySide": true,
  "diffEditor.ignoreTrimWhitespace": true,
  "diffEditor.experimental.showMoves": true,
  "editor.unicodeHighlight.invisibleCharacters": false,
  "editor.unicodeHighlight.ambiguousCharacters": false,
  "solidity.telemetry": false,
  // typescript
  "typescript.suggest.classMemberSnippets.enabled": true,
  "typescript.suggest.enabled": true,
  "typescript.suggest.completeFunctionCalls": true,
  "debug.disassemblyView.showSourceCode": false,
  "editor.formatOnPaste": true,
  "editor.formatOnType": true,

  "[jsonc]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[yaml]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[json]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[javascriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[css]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[html]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[typescriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[typescript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "diffEditor.codeLens": true
}"}
